definition.script:
pipeline {
  agent any
  tools {
    maven 'maven 3.9.2'
  }
    stages{
        stage('Build Maven'){
            steps{
                checkout([$class: 'GitSCM', branches: [[name: '*/main']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[url: 'https://github.com/fedoralepceska/kiii-project-final']]])
                sh 'mvn clean install'
            }
        }
        stage('Build docker image'){
            steps{
                script{
                    sh 'docker build -t flepceska/kiii-project .'
                }
            }
        }
        stage("Push image") {
            steps {
                withCredentials([string(credentialsId: 'dockerhub', variable: 'dockerhub')]) {
                    sh 'docker login -u flepceska -p ${dockerhub}'
                }
                sh 'docker push flepceska/kiii-project'
            }
        }
    }
}
